mapper.select:
        
			select pesgmv as totalsum
			from ds_emptrade aa
			where aa.year =#{year} and aa.month =#{month} and aa.employee_no =#{employee_no}

        
			select pesgmv,0 as pes_sendgmv ,pes_areagmv,pes_assigngmv,pes_pergmv
			from ds_emptrade aa
			where aa.year =#{year} and aa.month =#{month} and aa.employee_no =#{employee_no}
