mapper.select:
select humanstatus,cityname,storeno,storename,storeid,employeeno,username,zw, authorizedtype,professnallevel,remark,leavedate from ( SELECT humanstatus as humanstatus, hr.citySelect as cityname, store.name as storename, store.storeno as storeno, hr.store_id as storeid, hr.employee_no as employeeno, hr.name as username, hr.zw as zw, hr.authorizedtype as authorizedtype, hr.professnallevel as professnallevel, IFNULL(hr.remark,'') as remark, IFNULL(hr.leavedate,'') as leavedate, date_format(hr.update_time,'%Y-%m-%d') as update_time FROM t_humanresources hr LEFT JOIN t_store store ON hr.store_id = store.store_id ) hrstore
username like concat('%',#{username},'%')
and employeeno = #{employeeno}
and DATE_FORMAT(update_time,'%Y-%m-%d') between #{update_time_start} and #{update_time_end}
and storename =#{storename}
and storeid =#{storeid}
and zw =#{zw}
order by hrstore.update_time desc

SELECT humanchangeb.humanstatus,humanchangeb.cityname,humanchangeb.storename,t_store.storeno,humanchangeb.storeid, humanchangeb.employeeno,humanchangeb.username,humanchangeb.zw,humanchangeb.authorizedtype, humanchangeb.professnallevel,humanchangeb.remark,humanchangeb.leavedate FROM ( SELECT t_humanresources.humanstatus as humanstatus, t_humanresources.citySelect as cityname, t_humanresources.employee_no as employeeno, t_humanresources.name as username, t_humanresources.authorizedtype as authorizedtype, t_humanresources.professnallevel as professnallevel, IFNULL(t_humanresources.remark,'') as remark, IFNULL(t_humanresources.leavedate,'') as leavedate, IFNULL(IF(ISNULL(humanchangea.prestorename),t_humanresources.storename,humanchangea.prestorename),'') as storename, IFNULL(IF(ISNULL(humanchangea.prestore_id),t_humanresources.store_id,humanchangea.prestore_id),NULL) as storeid, IFNULL(IF(ISNULL(humanchangea.prezw),t_humanresources.zw,humanchangea.prezw),'') as zw, DATE_FORMAT(t_humanresources.update_time,'%Y-%m-%d') as update_time FROM t_humanresources LEFT JOIN ( SELECT t_humanresources_change.employee_no,store_id as prestore_id, storename as prestorename,zw as prezw FROM t_humanresources_change ,( SELECT min(id) as id, employee_no FROM t_humanresources_change WHERE DATE_FORMAT(changedate, '%Y-%m') = DATE_FORMAT(now(),'%Y-%m') GROUP BY employee_no ) humanchange WHERE humanchange.id = t_humanresources_change.id) humanchangea ON t_humanresources.employee_no = humanchangea.employee_no WHERE DATE_FORMAT(create_time, '%Y-%m') !=DATE_FORMAT(now(),'%Y-%m') ) humanchangeb ,t_store WHERE humanchangeb.storeid = t_store.store_id
and username like concat('%',#{username},'%')
and employeeno = #{employeeno}
and DATE_FORMAT(update_time,'%Y-%m-%d') between #{update_time_start} and #{update_time_end}
and storename =#{storename}
and storeid =#{storeid}
and zw =#{zw}
and humanstatus =#{humanstatus}
order by humanchangeb.update_time desc
