mapper.select:
select city_name, storeno,store_name, area_no, area_name, employee_a_no, employee_a_name, new_10_count ,pubseas from ds_areanewaddcus where year =#{year} and month =#{month}
and storeno IN (${storeids})
and area_no in (${areano})

mapper.insert:

	INSERT INTO ds_areanewaddcus (
		city_name, storeno,store_name, area_no, area_name,
		employee_a_no, employee_a_name, new_10_count,year,month,createtime
	) VALUES(
		#{city_name},#{storeno},#{store_name},#{area_no},#{area_name},
		#{employee_a_no},#{employee_a_name},#{new_10_count},#{year},#{month},#{createtime}
	)
