mapper.select:
select city_name, storeno,store_name,new_10_count,pubseas from ds_areanewaddcus_store where year =#{year} and month =#{month}
and storeno IN (${storeids})
and area_no in (${areano})

mapper.insert:

	INSERT INTO ds_areanewaddcus_store (
		city_name, storeno,store_name,new_10_count, year, month,createtime
	) VALUES(
		#{city_name},#{storeno},#{store_name},#{new_10_count},#{year},#{month},
		#{createtime}
	)

mapper.update:

	update ds_newaddcus aa
	left join ds_areanewaddcus_store bb on (aa.storeno = bb.storeno and aa.`year` = bb.`year` and aa.`month` = bb.`month`)
	left join ds_areapubseas_newaddcus_store cc on (aa.storeno = cc.storeno and aa.`year` = cc.`year` and aa.`month` = cc.`month`)
	set bb.pubseas = aa.new_10_count - ifnull(bb.new_10_count,0) - ifnull(cc.new_10_count,0) 
	where aa.`year` =#{year}
	and aa.`month`=#{month}
