mapper.select:
select city_name, storeno,store_name, area_no, area_name,employee_a_no, employee_a_name, pesgmv, other_order_amount,other_order_count,other_returned_amount, other_returned_count,pubseas from ds_areatrade where year =#{year} and month =#{month}
and storeno IN (${storeids})
and area_no in (${areano})

mapper.insert:

	INSERT INTO ds_areatrade (
		city_name, storeno,store_name, area_no, area_name,employee_a_no,
		employee_a_name, pesgmv, other_order_amount,other_order_count,other_returned_amount,
		other_returned_count,year,month,createtime
	) VALUES(
		#{city_name},#{storeno},#{store_name},#{area_no},#{area_name},#{employee_a_no},
		#{employee_a_name},#{pesgmv},#{other_order_amount},#{other_order_count},#{other_returned_amount},
		#{other_returned_count},#{year},#{month},#{createtime}
	)
