mapper.select:
select city_name,storeno,store_name, pesgmv, other_order_amount,other_order_count, other_returned_amount,other_returned_count,pubseas from ds_areatrade_store where year =#{year} and month =#{month}
and storeno IN (${storeids})
and area_no in (${areano})

mapper.insert:

	INSERT INTO ds_areatrade_store (
		city_name,storeno,store_name,  pesgmv, other_order_amount,other_order_count,other_returned_amount,
		other_returned_count,year,month,createtime
	) VALUES(
		#{city_name},#{storeno},#{store_name},#{pesgmv},#{other_order_amount},#{other_order_count},#{other_returned_amount},
		#{other_returned_count},#{year},#{month},#{createtime}
	)
