mapper.select:
select city_name, platformid, IFNULL(storeno,'') AS storeno, store_name, pay_count, new_count, total_count, pay_20_count, new_20_count, new_10_count, pay_10_count, date_format(createtime,'%Y-%m-%d %h:%i') as createtime from ds_newaddcus where year =#{year} and month =#{month}
and storeno IN (${storeids})
LIMIT ${limitcond}
